// src/api/ai.ts
import { SUPABASE_URL, SUPABASE_ANON_KEY } from '@/constants/Environment';

export async function generateAIReply(params: {
  userId: string;
  personId: string;
  personName: string;
  relationshipType?: string | null;
  messages: { role: 'user' | 'assistant'; content: string }[];
}): Promise<string> {
  try {
    const response = await fetch(`${SUPABASE_URL}/functions/v1/generate-ai-response`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${SUPABASE_ANON_KEY}`,
      },
      body: JSON.stringify({
        user_id: params.userId,
        person_id: params.personId,
        person_name: params.personName,
        relationship_type: params.relationshipType ?? null,
        messages: params.messages,
      }),
    });

    if (!response.ok) {
      console.error(`generateAIReply failed with status: ${response.status}`);
      return "I'm having trouble replying right now, but your feelings matter. Please try again in a moment.";
    }

    const data = await response.json();

    if (data && data.reply) {
      return data.reply;
    } else {
      console.error("generateAIReply missing reply in response:", data);
      return "I'm having trouble replying right now, but your feelings matter. Please try again in a moment.";
    }
  } catch (error) {
    console.error("Error in generateAIReply:", error);
    return "I'm having trouble replying right now, but your feelings matter. Please try again in a moment.";
  }
}
```

