  user_id: string;
  person_id: string;
  sender: 'user' | 'ai';
  content: string;
  created_at: string;
}

export default function ChatScreen() {
  const theme = useTheme();
  const [messages, setMessages] = useState<Message[]>([]);
  const [input, setInput] = useState('');
  const [isWaitingForAI, setIsWaitingForAI] = useState(false);
  const { currentUser } = useAuth();

  // Placeholder data - replace with actual person data
  const person_id = '1';
  const person_name = 'John Doe';
  const relationship_type = 'Friend';

  useEffect(() => {
    // Fetch messages from Supabase here
    async function fetchMessages() {
      // Implement your message fetching logic here
    }
    fetchMessages();
  }, []);

  const sendMessage = async () => {
    if (!input.trim()) return;

    // 1. Insert user message into public.messages (Optimistic update)
    const userMessage = {
      id: Math.random().toString(), // Temporary ID
      user_id: currentUser.id,
      person_id: person_id,
      sender: 'user',
      content: input.trim(),
      created_at: new Date().toISOString(),
    };

    setMessages(prevMessages => [...prevMessages, userMessage]);
    setInput('');
    setIsWaitingForAI(true);

    try {
      await supabase.from('messages').insert({
        user_id: currentUser.id,
        person_id: person_id,
        sender: 'user',
        content: input.trim(),
      });

      // 2. Call generateAIReply
      const replyText = await generateAIReply({
        userId: currentUser.id,
        personId: person_id,
        personName: person_name,
        relationshipType: relationship_type,
        messages: messages.slice(-10).map(m => ({ role: m.sender === 'user' ? 'user' : 'assistant', content: m.content })),
      });

      // 3. Insert AI reply into public.messages
      const aiMessage = {
        id: Math.random().toString(), // Temporary ID
        user_id: currentUser.id,
        person_id: person_id,
        sender: 'ai',
        content: replyText,
        created_at: new Date().toISOString(),
      };
      setMessages(prevMessages => [...prevMessages, aiMessage]);

      await supabase.from('messages').insert({
        user_id: currentUser.id,
        person_id: person_id,
        sender: 'ai',
        content: replyText,
      });
    } catch (e) {
      console.error("Error sending message:", e);
      // 4. Still show a single AI bubble with the fallback replyText
      const fallbackMessage = {
        id: Math.random().toString(), // Temporary ID
        user_id: currentUser.id,
        person_id: person_id,
        sender: 'ai',
        content: "I'm having trouble replying right now, but your feelings matter.",
        created_at: new Date().toISOString(),
      };
      setMessages(prevMessages => [...prevMessages, fallbackMessage]);
    } finally {
      setIsWaitingForAI(false);
    }
  };

  return (
    <View style={[styles.container, { backgroundColor: theme.colors.background }]}>
      <FlatList
        data={messages}
        renderItem={({ item }) => (
          <View style={item.sender === 'user' ? styles.userMessage : styles.aiMessage}>
            <Text style={{ color: theme.colors.text }}>{item.content}</Text>
          </View>
        )}
        keyExtractor={item => item.id}
      />
      <View style={styles.inputContainer}>
        <TextInput
          style={[styles.input, { color: theme.colors.text }]}
          value={input}
          onChangeText={setInput}
          placeholder="Type your message..."
          placeholderTextColor={theme.colors.text}
        />
        <Button title="Send" onPress={sendMessage} disabled={isWaitingForAI} />
      </View>
      {isWaitingForAI && <Text style={{ color: theme.colors.primary }}>Typing...</Text>}
    </View>
  );
}

