import "jsr:@supabase/functions-js/edge-runtime.d.ts";

const OPENAI_API_KEY = Deno.env.get('OPENAI_API_KEY');

interface Message {
  role: 'user' | 'assistant';
  content: string;
}

interface RequestBody {
  person_id: string;
  messages: Message[];
}

Deno.serve(async (req: Request) => {
  try {
    // Parse request body
    const { person_id, messages }: RequestBody = await req.json();

    console.log('Received request for person:', person_id);
    console.log('Message history length:', messages.length);

    if (!OPENAI_API_KEY) {
      throw new Error('OPENAI_API_KEY not configured');
    }

    // Build the system prompt
    const systemPrompt = `You are a compassionate and empathetic AI therapist helping someone process their feelings about a person in their life. 

Your role is to:
- Listen actively and validate their feelings
- Ask thoughtful questions to help them explore their emotions
- Provide gentle insights when appropriate
- Help them understand relationship dynamics
- Never judge or criticize
- Be supportive and encouraging

Keep responses concise (2-3 sentences) and conversational.`;

    // Prepare messages for OpenAI
    const openaiMessages = [
      { role: 'system', content: systemPrompt },
      ...messages,
    ];

    // Call OpenAI API
    const response = await fetch('https://api.openai.com/v1/chat/completions', {
      method: 'POST',
      headers: {
        'Authorization': `Bearer ${OPENAI_API_KEY}`,
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        model: 'gpt-4o-mini',
        messages: openaiMessages,
        temperature: 0.8,
        max_tokens: 200,
      }),
    });

    if (!response.ok) {
      const error = await response.text();
      console.error('OpenAI API error:', error);
      throw new Error(`OpenAI API error: ${response.status}`);
    }

    const data = await response.json();
    const reply = data.choices[0].message.content;

    console.log('Generated reply:', reply);

    return new Response(
      JSON.stringify({ reply }),
      {
        headers: {
          'Content-Type': 'application/json',
          'Connection': 'keep-alive'
        },
        status: 200,
      }
    );
  } catch (error) {
    console.error('Error in generate-ai-response:', error);
    return new Response(
      JSON.stringify({ 
        error: error.message,
        reply: 'I apologize, but I\'m having trouble responding right now. Please try again.' 
      }),
      {
        headers: {
          'Content-Type': 'application/json',
        },
        status: 500,
      }
    );
  }
});

