**Example 3: Chat Screen (app/chat/[personId].tsx)**

```typescript
// app/chat/[personId].tsx
import React, { useState, useEffect } from 'react';
import { View, Text, FlatList, TextInput, Button, StyleSheet } from 'react-native';
import { useLocalSearchParams } from 'expo-router';
import { supabase } from '../../utils/supabase';

export default function ChatScreen() {
  const { personId } = useLocalSearchParams();
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');

  useEffect(() => {
    async function fetchMessages() {
      const { data, error } = await supabase
        .from('messages')
        .select('*')
        .eq('user_id', supabase.auth.currentUser?.id)
        .eq('person_id', personId)
        .order('created_at', { ascending: true });

      if (error) {
        console.error('Error fetching messages:', error);
      } else {
        setMessages(data || []);
      }
    }

    fetchMessages();
  }, [personId]);

  const sendMessage = async () => {
    if (newMessage.trim() === '') return;

    const userMessage = {
      user_id: supabase.auth.currentUser?.id,
      person_id: personId,
      sender: 'user',
      content: newMessage,
    };

    // 1. Insert user message
    const { data: userMessageData, error: userMessageError } = await supabase
      .from('messages')
      .insert([userMessage])
      .select('*')
      .single();

    if (userMessageError) {
      console.error('Error sending message:', userMessageError);
      return;
    }

    setMessages([...messages, userMessageData]);
    setNewMessage('');

    // 2. Call Edge Function
    const { data: aiResponse, error: aiError } = await supabase.functions.invoke('generate-ai-response', {
      body: {
        person_id: personId,
        messages: [...messages, userMessageData].map(m => ({ sender: m.sender, content: m.content })),
      },
    });

    if (aiError) {
      console.error('Error calling edge function:', aiError);
      return;
    }

    // 3. Insert AI response
    const aiMessage = {
      user_id: supabase.auth.currentUser?.id,
      person_id: personId,
      sender: 'ai',
      content: aiResponse.reply,
    };

    const { data: aiMessageData, error: aiMessageError } = await supabase
      .from('messages')
      .insert([aiMessage])
      .select('*')
      .single();

    if (aiMessageError) {
      console.error('Error inserting AI message:', aiMessageError);
      return;
    }

    setMessages(prevMessages => [...prevMessages, aiMessageData]);
  };

  return (
    <View style={styles.container}>
      <FlatList
        data={messages}
        renderItem={({ item }) => (
          <View style={item.sender === 'user' ? styles.userMessage : styles.aiMessage}>
            <Text>{item.content}</Text>
          </View>
        )}
        keyExtractor={(item) => item.id}
      />
      <View style={styles.inputContainer}>
        <TextInput
          style={styles.input}
          value={newMessage}
          onChangeText={setNewMessage}
          placeholder="Type your message..."
        />
        <Button title="Send" onPress={sendMessage} />
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 10,
  },
  userMessage: {
    backgroundColor: '#DCF8C6',
    padding: 8,
    borderRadius: 8,
    alignSelf: 'flex-end',
    marginBottom: 5,
  },
  aiMessage: {
    backgroundColor: '#ECE5DD',
    padding: 8,
    borderRadius: 8,
    alignSelf: 'flex-start',
    marginBottom: 5,
  },
  inputContainer: {
    flexDirection: 'row',
    alignItems: 'center',
    marginTop: 10,
  },
  input: {
    flex: 1,
    borderWidth: 1,
    borderColor: '#ccc',
    borderRadius: 8,
    padding: 8,
    marginRight: 8,
  },
});
```

