import React, { createContext, useState, useEffect, useContext } from 'react';
import AsyncStorage from '@react-native-async-storage/async-storage';
import { Theme } from '@react-navigation/native';
import { Colors } from '../constants/Colors';

export type ThemeKey = 'OceanBlue' | 'SoftRose' | 'ForestGreen' | 'SunnyYellow';

export interface ThemeContextType {
  themeKey: ThemeKey;
  theme: Theme;
  setTheme: (themeKey: ThemeKey) => void;
}

const ThemeContext = createContext<ThemeContextType | undefined>(undefined);

const themes: { [key in ThemeKey]: Theme } = {
  OceanBlue: {
    dark: false,
    colors: {
      primary: '#007AFF', // Example color
      background: '#EBF4FA',
      card: '#FFFFFF',
      text: '#000000',
      border: '#D0D7DE',
      notification: '#FF3B30',
    },
  },
  SoftRose: {
    dark: false,
    colors: {
      primary: '#FF69B4',
      background: '#FAF0E6',
      card: '#FFFFFF',
      text: '#000000',
      border: '#D0D7DE',
      notification: '#FF3B30',
    },
  },
  ForestGreen: {
    dark: false,
    colors: {
      primary: '#228B22',
      background: '#F0FFF0',
      card: '#FFFFFF',
      text: '#000000',
      border: '#D0D7DE',
      notification: '#FF3B30',
    },
  },
  SunnyYellow: {
    dark: false,
    colors: {
      primary: '#FFD700',
      background: '#FFFACD',
      card: '#FFFFFF',
      text: '#000000',
      border: '#D0D7DE',
      notification: '#FF3B30',
    },
  },
};

export const ThemeProvider: React.FC<{ children: React.ReactNode }> = ({ children }) => {
  const [themeKey, setThemeKey] = useState<ThemeKey>('OceanBlue');
  const [theme, setTheme] = useState<Theme>(themes[themeKey]);

  useEffect(() => {
    const loadTheme = async () => {
      try {
        const storedThemeKey = await AsyncStorage.getItem('themeKey') as ThemeKey | null;
        if (storedThemeKey) {
          setThemeKey(storedThemeKey);
        }
      } catch (error) {
        console.error('Error loading theme from AsyncStorage:', error);
      }
    };

    loadTheme();
  }, []);

  useEffect(() => {
    setTheme(themes[themeKey]);
    AsyncStorage.setItem('themeKey', themeKey);
  }, [themeKey]);

  const contextValue: ThemeContextType = {
    themeKey,
    theme,
    setTheme: (newThemeKey: ThemeKey) => {
      setThemeKey(newThemeKey);
    },
  };

  return (
    <ThemeContext.Provider value={contextValue}>
      {children}
    </ThemeContext.Provider>
  );
};

export const useTheme = () => {
  const context = useContext(ThemeContext);
  if (!context) {
    throw new Error('useTheme must be used within a ThemeProvider');
  }
  return context;
};
```

