```typescript
// components/button.tsx
import React from "react";
import {
  ActivityIndicator,
  Pressable,
  StyleSheet,
  Text,
  TextStyle,
  ViewStyle,
} from "react-native";
import { useTheme } from "@react-navigation/native";
import { LinearGradient } from "expo-linear-gradient"; // Import LinearGradient

type ButtonVariant = "primary" | "secondary" | "outline" | "ghost"; // Modified variants
type ButtonSize = "sm" | "md" | "lg";

interface ButtonProps {
  onPress?: () => void;
  variant?: ButtonVariant;
  size?: ButtonSize;
  disabled?: boolean;
  loading?: boolean;
  children?: React.ReactNode;
  title?: string; // Added title prop
  style?: ViewStyle;
  textStyle?: TextStyle;
}

export const Button: React.FC<ButtonProps> = ({
  onPress,
  variant = "primary", // Default to primary
  size = "md",
  disabled = false,
  loading = false,
  title, // Use title prop
  children,
  style,
  textStyle,
}) => {
  const { theme } = useTheme();

  const sizeStyles: Record<
    ButtonSize,
    { height: number; fontSize: number; padding: number }
  > = {
    sm: { height: 36, fontSize: 14, padding: 12 },
    md: { height: 44, fontSize: 16, padding: 16 },
    lg: { height: 55, fontSize: 18, padding: 20 },
  };

  const getVariantStyle = () => {
    const baseStyle: ViewStyle = {
      borderRadius: 25, // Rounded pill shape
      flexDirection: "row",
      alignItems: "center",
      justifyContent: "center",
      overflow: "hidden", // Clip content for rounded corners
    };

    switch (variant) {
      case "primary":
        return {
          ...baseStyle,
        };
      case "outline":
        return {
          ...baseStyle,
          backgroundColor: "transparent",
          borderWidth: 2,
          borderColor: theme.colors.primary,
        };
      case "secondary":
        return {
          ...baseStyle,
          backgroundColor: theme.colors.card,
        };
      case "ghost":
        return {
          ...baseStyle,
          backgroundColor: "transparent",
        };
    }
  };

  const getTextColor = () => {
    if (disabled) {
      return theme.colors.text;
    }

    switch (variant) {
      case "primary":
        return theme.colors.background;
      case "outline":
        return theme.colors.primary;
      case "secondary":
        return theme.colors.text;
      case "ghost":
        return theme.colors.primary;
    }
  };

  const renderContent = () => {
    if (loading) {
      return <ActivityIndicator color={getTextColor()} />;
    }

    return (
      <Text
        style={StyleSheet.flatten([
          {
            fontSize: sizeStyles[size].fontSize,
            color: getTextColor(),
            textAlign: "center",
            marginBottom: 0,
            fontWeight: "700",
          },
          textStyle,
        ])}
      >
        {title || children}
      </Text>
    );
  };

  return (
    <Pressable
      onPress={onPress}
      disabled={disabled || loading}
      style={[
        getVariantStyle(),
        {
          height: sizeStyles[size].height,
          paddingHorizontal: sizeStyles[size].padding,
          opacity: disabled ? 0.5 : 1,
        },
        style,
      ]}
    >
      {variant === "primary" ? (
        <LinearGradient
          colors={[theme.colors.primary, theme.colors.background]} // Use theme colors for gradient
          style={StyleSheet.absoluteFill}
          start={{ x: 0, y: 0 }}
          end={{ x: 1, y: 0 }}
        />
      ) : null}
      {renderContent()}
    </Pressable>
  );
};

export default Button;
