```typescript
// utils/aiHelpers.ts
import { supabase } from '@/lib/supabase';
import { showErrorToast } from '@/utils/toast';

const generateAIReply = async ({ user_id, person_id, person_name, relationship_type, messages }) => {
  try {
    const response = await supabase.functions.invoke('generate-ai-response', {
      body: {
        user_id,
        person_id,
        person_name,
        relationship_type,
        messages: messages.map(m => ({ role: m.role === 'user' ? 'user' : 'assistant', content: m.content })),
      },
    });

    if (response.error) {
      showErrorToast("AI Reply Failed");
      return "I'm having trouble replying right now, but your feelings matter.";
    }

    return response.data.reply || "I'm having trouble replying right now, but your feelings matter.";
  } catch (e) {
    showErrorToast("AI Reply Failed");
    return "I'm having trouble replying right now, but your feelings matter.";
  }
};
