// supabase/functions/generate-ai-response/index.ts
import { serve } from "https://deno.land/std@0.177.0/http/server.ts";

const OPENAI_API_KEY = Deno.env.get("OPENAI_API_KEY");
const OPENAI_API_URL = "https://api.openai.com/v1/chat/completions";

const SYSTEM_PROMPT = `
You are "Safe Space", a warm, trauma-aware relationship and emotional health coach.

Always:
1. Start with 1 short sentence that validates the user's feelings.
2. Then give 2–4 clear, practical pieces of guidance (not just reflection).
3. Keep a conversational tone, like a kind, intelligent friend.
4. You can gently challenge unhelpful patterns, but never shame the user.
`.trim();

const corsHeaders = {
  "Access-Control-Allow-Origin": "*",
  "Access-Control-Allow-Methods": "POST, OPTIONS",
  "Access-Control-Allow-Headers": "authorization, x-client-info, apikey, content-type",
};

serve(async (req) => {
  // Handle CORS preflight
  if (req.method === "OPTIONS") {
    return new Response(null, {
      status: 200,
      headers: corsHeaders,
    });
  }

  if (req.method !== "POST") {
    return new Response(
      JSON.stringify({ error: "Method not allowed" }),
      { 
        status: 405, 
        headers: { 
          "Content-Type": "application/json",
          ...corsHeaders,
        } 
      },
    );
  }

  if (!OPENAI_API_KEY) {
    console.error("[Edge] Missing OPENAI_API_KEY");
    return new Response(
      JSON.stringify({ error: "Server misconfiguration: missing OPENAI_API_KEY" }),
      { 
        status: 500, 
        headers: { 
          "Content-Type": "application/json",
          ...corsHeaders,
        } 
      },
    );
  }

  // 1) Parse incoming JSON safely
  let body: any;
  try {
    body = await req.json();
  } catch (err) {
    console.error("[Edge] Failed to parse request JSON:", err);
    return new Response(
      JSON.stringify({ error: "Invalid JSON body from client" }),
      { 
        status: 400, 
        headers: { 
          "Content-Type": "application/json",
          ...corsHeaders,
        } 
      },
    );
  }

  const { messages, personId, personName, personRelationshipType } = body ?? {};

  if (!Array.isArray(messages)) {
    console.error("[Edge] Missing or invalid 'messages' array in request body");
    return new Response(
      JSON.stringify({ error: "Missing or invalid 'messages' in request body" }),
      { 
        status: 400, 
        headers: { 
          "Content-Type": "application/json",
          ...corsHeaders,
        } 
      },
    );
  }

  // 2) Build the system message with context
  const systemMessage = {
    role: "system" as const,
    content: `
${SYSTEM_PROMPT}

The person being discussed is: ${personName || "Unknown"}
Relationship type: ${personRelationshipType || "Unknown"}

When responding, speak directly to the user about this relationship.
`.trim(),
  };

  // 3) Convert messages to OpenAI format
  const openaiMessages = messages.map((msg: any) => ({
    role: msg.sender === "user" ? "user" : "assistant",
    content: msg.content,
  }));

  try {
    // 4) Call OpenAI
    const openaiRes = await fetch(OPENAI_API_URL, {
      method: "POST",
      headers: {
        "Authorization": `Bearer ${OPENAI_API_KEY}`,
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        model: "gpt-4o-mini",
        messages: [systemMessage, ...openaiMessages],
        temperature: 0.7,
      }),
    });

    // Read as text FIRST so we can log it, then parse
    const rawText = await openaiRes.text();

    if (!openaiRes.ok) {
      console.error(
        "[Edge] OpenAI returned non-2xx:",
        openaiRes.status,
        rawText,
      );
      return new Response(
        JSON.stringify({
          error: "OpenAI API error",
          status: openaiRes.status,
          details: rawText || "No response body",
        }),
        { 
          status: 500, 
          headers: { 
            "Content-Type": "application/json",
            ...corsHeaders,
          } 
        },
      );
    }

    let data: any;
    try {
      data = rawText ? JSON.parse(rawText) : null;
    } catch (err) {
      console.error("[Edge] Failed to parse OpenAI JSON:", err, rawText);
      return new Response(
        JSON.stringify({
          error: "Failed to parse OpenAI response",
          details: rawText || "Empty response body",
        }),
        { 
          status: 500, 
          headers: { 
            "Content-Type": "application/json",
            ...corsHeaders,
          } 
        },
      );
    }

    const reply =
      data?.choices?.[0]?.message?.content ??
      "I'm not sure what to say yet, but I'm here with you.";

    return new Response(
      JSON.stringify({ reply }),
      { 
        status: 200, 
        headers: { 
          "Content-Type": "application/json",
          ...corsHeaders,
        } 
      },
    );
  } catch (err) {
    console.error("[Edge] Unexpected error while calling OpenAI:", err);
    return new Response(
      JSON.stringify({ error: "Unexpected server error", details: String(err) }),
      { 
        status: 500, 
        headers: { 
          "Content-Type": "application/json",
          ...corsHeaders,
        } 
      },
    );
  }
});

