import React, { useState } from 'react';
import { View, Text, TextInput, Button, ScrollView, KeyboardAvoidingView, Platform } from 'react-native';
import { useTheme } from '@react-navigation/native';
import SwipeableModal from 'react-native-swipeable-modal'; // Assuming this is the correct import
import { createClient } from '@supabase/supabase-js'; // Replace with your actual Supabase client

const supabaseUrl = process.env.EXPO_PUBLIC_SUPABASE_URL; // Replace with your actual Supabase URL
const supabaseKey = process.env.EXPO_PUBLIC_SUPABASE_ANON_KEY; // Replace with your actual Supabase Anon Key
const supabase = createClient(supabaseUrl, supabaseKey);

function ErrorBoundary({ children }) {
  const [hasError, setHasError] = useState(false);

  if (hasError) {
    return (
      <View>
        <Text>Something went wrong.</Text>
      </View>
    );
  }

  return children;
}

export default function HomeScreen() {
  const theme = useTheme();
  const [showAddModal, setShowAddModal] = useState(false);
  const [name, setName] = useState('');
  const [relationshipType, setRelationshipType] = useState('');
  const [nameError, setNameError] = useState('');
  const [saving, setSaving] = useState(false);

  const handleCloseModal = () => {
    setShowAddModal(false);
    setName('');
    setRelationshipType('');
    setNameError('');
  };

  const handleSave = async () => {
    if (!name.trim()) {
      setNameError('Name is required');
      return;
    }

    setSaving(true);
    try {
      const { data, error } = await supabase
        .from('persons') // Replace 'persons' with your actual table name
        .insert([{ user_id: 'your_user_id', name, relationship_type }]); // Replace 'your_user_id' with the actual user ID

      if (error) {
        console.error('Error inserting person:', error);
        // Handle the error appropriately, e.g., show an error message to the user
      } else {
        console.log('Person inserted successfully:', data);
        // Refresh the list of persons
        handleCloseModal();
      }
    } catch (error) {
      console.error('An unexpected error occurred:', error);
      // Handle the error appropriately
    } finally {
      setSaving(false);
    }
  };

  return (
    <ErrorBoundary>
      <View style={{ flex: 1, backgroundColor: theme.colors.background }}>
        {/* Other UI elements */}
        <Button title="Add Person" onPress={() => setShowAddModal(true)} />

        <SwipeableModal
          visible={showAddModal}
          onClose={handleCloseModal}
        >
          <KeyboardAvoidingView
            style={{ maxHeight: '85%' }}
            behavior={Platform.OS === 'ios' ? 'padding' : 'height'}
          >
            <ScrollView contentContainerStyle={{ padding: 20 }}>
              <Text>Add Person</Text>

              <Text>Name *</Text>
              <TextInput
                value={name}
                onChangeText={(text) => {
                  setName(text);
                  if (nameError && text.trim()) setNameError('');
                }}
                placeholder="Enter their name"
                autoCapitalize="words"
                maxLength={50}
              />
              {nameError ? <Text style={{ color: 'red' }}>{nameError}</Text> : null}

              <Text>Relationship Type</Text>
              <TextInput
                value={relationshipType}
                onChangeText={setRelationshipType}
                placeholder="partner, ex, friend, parent..."
                autoCapitalize="words"
                maxLength={50}
              />

              <Button title="Cancel" onPress={handleCloseModal} />
              <Button title="Save" onPress={handleSave} disabled={saving} />
            </ScrollView>
          </KeyboardAvoidingView>
        </SwipeableModal>
      </View>
    </ErrorBoundary>
  );
}
```

