import "jsr:@supabase/functions-js/edge-runtime.d.ts";
import { createClient } from "npm:@supabase/supabase-js@2";

type AllowedSize = "1024x1024" | "1536x1024" | "1024x1536";

type GenRequest = {
  prompt: string;
  size?: AllowedSize;
};

type GenResponse = {
  url: string;
  path: string;
  width: number;
  height: number;
  duration_ms: number;
  provider_id?: string;
};

const SUPABASE_URL = Deno.env.get("SUPABASE_URL")!;
const SERVICE_ROLE = Deno.env.get("SUPABASE_SERVICE_ROLE_KEY")!;
const BUCKET = "generated-images";
const OPENAI_API_KEY = Deno.env.get("OPENAI_API_KEY")!;
const OPENAI_BASE_URL = "https://api.openai.com/v1";

const supabase = createClient(SUPABASE_URL, SERVICE_ROLE, { auth: { persistSession: false } });

const DEFAULT_SIZE: AllowedSize = "1024x1024";
const ALLOWED_SIZES: AllowedSize[] = ["1024x1024", "1536x1024", "1024x1536"];

function parseSize(size?: string): { w: number; h: number; sizeStr: AllowedSize } | null {
  const s = (size ?? DEFAULT_SIZE) as AllowedSize;
  switch (s) {
    case "1024x1024": return { w: 1024, h: 1024, sizeStr: s };
    case "1536x1024": return { w: 1536, h: 1024, sizeStr: s };
    case "1024x1536": return { w: 1024, h: 1536, sizeStr: s };
    default: return null;
  }
}

Deno.serve(async (req) => {
  try {
    if (req.method !== "POST") return new Response("Method Not Allowed", { status: 405 });

    const auth = req.headers.get("Authorization") || "";
    const { data: user } = await supabase.auth.getUser(auth.replace("Bearer ", ""));
    if (!user?.user) return new Response(JSON.stringify({ error: "Unauthorized" }), { status: 401 });

    const body = (await req.json()) as GenRequest;
    if (!body.prompt || body.prompt.trim().length < 3) {
      return new Response(JSON.stringify({ error: "Prompt required" }), { status: 400 });
    }

    const parsed = parseSize(body.size);
    if (!parsed) {
      return new Response(
        JSON.stringify({ error: "Invalid size", allowed: ALLOWED_SIZES }),
        { status: 400 }
      );
    }
    const { w, h, sizeStr } = parsed;

    const started = performance.now();

    const genRes = await fetch(`${OPENAI_BASE_URL}/images/generations`, {
      method: "POST",
      headers: {
        "Authorization": `Bearer ${OPENAI_API_KEY}`,
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        model: "dall-e-3",
        prompt: body.prompt,
        size: sizeStr,
        n: 1,
        quality: "standard",
        response_format: "url",
      }),
    });

    if (!genRes.ok) {
      const t = await genRes.text();
      return new Response(JSON.stringify({ error: "Provider error", detail: t }), { status: 502 });
    }
    const genJson: any = await genRes.json();

    let binary: Uint8Array;
    if (genJson.data?.[0]?.url) {
      const imageUrl = genJson.data[0].url as string;
      const imageRes = await fetch(imageUrl);
      if (!imageRes.ok) {
        return new Response(JSON.stringify({ error: "Failed to fetch generated image" }), { status: 502 });
      }
      binary = new Uint8Array(await imageRes.arrayBuffer());
    } else {
      return new Response(JSON.stringify({ error: "Unknown provider response" }), { status: 500 });
    }

    const ext = "png";
    const fileName = `${crypto.randomUUID()}.${ext}`;
    const path = `${user.user.id}/${Date.now()}-${fileName}`;

    const { error: upErr } = await supabase.storage.from(BUCKET).upload(path, binary, {
      contentType: `image/${ext}`,
      upsert: false,
    });
    if (upErr) {
      return new Response(JSON.stringify({ error: "Upload failed", detail: upErr.message }), { status: 500 });
    }

    const publicUrl = supabase.storage.from(BUCKET).getPublicUrl(path).data.publicUrl;

    const duration_ms = Math.round(performance.now() - started);
    const response: GenResponse = {
      url: publicUrl,
      path,
      width: w,
      height: h,
      duration_ms,
      provider_id: genJson.id ?? undefined,
    };

    return new Response(JSON.stringify(response), { headers: { "Content-Type": "application/json" }, status: 200 });
  } catch (e) {
    return new Response(JSON.stringify({ error: "Unhandled error", detail: String(e) }), { status: 500 });
  }
});

