// components/ui/AddPersonSheet.tsx
import React, { useState, useRef, useEffect } from 'react';
import { Modal, View, Text, TextInput, Pressable, ScrollView, KeyboardAvoidingView, Platform, SafeAreaView } from 'react-native';
import { Ionicons } from '@expo/vector-icons';
import { useSafeAreaInsets } from 'react-native-safe-area-context';
import { useTheme } from '../../contexts/ThemeContext';
import { insertPerson } from '../../lib/supabase'; // Placeholder
import { showSuccessToast, showErrorToast } from '../../utils/toast'; // Placeholder

interface AddPersonSheetProps {
  visible: boolean;
  onClose: () => void;
  onSaved: () => void;
  userId: string;
  theme: any;
  insets: any;
}

const AddPersonSheet: React.FC<AddPersonSheetProps> = ({ visible, onClose, onSaved, userId, theme, insets }) => {
  const [name, setName] = useState('');
  const [relationshipType, setRelationshipType] = useState('');
  const [error, setError] = useState('');
  const scrollRef = useRef<ScrollView>(null);

  useEffect(() => {
    if (visible) {
      setName('');
      setRelationshipType('');
      setError('');
    }
  }, [visible]);

  const handleSave = async () => {
    if (!name.trim()) {
      setError('Name is required');
      return;
    }

    try {
      await insertPerson({ userId, name: name.trim(), relationship_type: relationshipType.trim() || null });
      showSuccessToast('Person added!');
      onSaved();
      onClose();
    } catch (e: any) {
      showErrorToast(e.message || 'Failed to add person.');
    }
  };

  return (
    <Modal transparent={true} animationType="slide" presentationStyle="overFullScreen" visible={visible}>
      <Pressable style={{ flex: 1, backgroundColor: 'rgba(0,0,0,0.4)' }} onPress={onClose} />
      <SafeAreaView style={{ position: 'absolute', bottom: 0, backgroundColor: '#FFFFFF', borderTopLeftRadius: 24, borderTopRightRadius: 24, paddingTop: 16, paddingBottom: insets.bottom }}>
        <KeyboardAvoidingView behavior={Platform.OS === 'ios' ? 'padding' : undefined}>
          <ScrollView keyboardShouldPersistTaps="handled" showsVerticalScrollIndicator={false} contentContainerStyle={{ paddingBottom: 160 }}>
            <View style={{ flexDirection: 'row', justifyContent: 'space-between', alignItems: 'center', paddingHorizontal: 16 }}>
              <Text style={{ fontSize: 18, fontWeight: 'bold' }}>Add Person</Text>
              <Pressable onPress={onClose}>
                <Ionicons name="close" size={24} color="black" />
              </Pressable>
            </View>

            <View style={{ paddingHorizontal: 16, marginTop: 16 }}>
              <Text style={{ fontSize: 12, color: 'gray' }}>Name (required)</Text>
              <TextInput
                style={{ borderWidth: error ? 1 : 0, borderColor: 'red', borderRadius: 8, padding: 10, backgroundColor: '#F5F5F5' }}
                placeholder="Enter their name"
                value={name}
                onChangeText={setName}
                onFocus={() => requestAnimationFrame(() => scrollRef.current?.scrollTo({ y: 120, animated: true }))}
              />
              {error ? <Text style={{ color: 'red', marginTop: 4 }}>{error}</Text> : null}
            </View>

            <View style={{ paddingHorizontal: 16, marginTop: 16 }}>
              <Text style={{ fontSize: 12, color: 'gray' }}>Relationship Type (optional)</Text>
              <TextInput
                style={{ borderRadius: 8, padding: 10, backgroundColor: '#F5F5F5' }}
                placeholder="partner, ex, friend, parent..."
                value={relationshipType}
                onChangeText={setRelationshipType}
                onFocus={() => requestAnimationFrame(() => scrollRef.current?.scrollTo({ y: 120, animated: true }))}
              />
            </View>
          </ScrollView>

          <View style={{ flexDirection: 'row', justifyContent: 'space-around', padding: 16 }}>
            <Pressable onPress={onClose} style={{ backgroundColor: 'lightgray', padding: 12, borderRadius: 8, flex: 1, alignItems: 'center' }}>
              <Text>Cancel</Text>
            </Pressable>
            <Pressable onPress={handleSave} style={{ backgroundColor: 'lightblue', padding: 12, borderRadius: 8, flex: 1, alignItems: 'center' }}>
              <Text>Save</Text>
            </Pressable>
          </View>
        </KeyboardAvoidingView>
      </SafeAreaView>
    </Modal>
  );
};

export default AddPersonSheet;
